%% Teaching from classic papers: Hill’s model of muscle%  Jeffrey W. Holmes, Department of Biomedical Engineering, Columbia University, New York%  Roshni, BE17B009%  Run sections separately.%%clear;% EXAMPLE 1: Response of Hill's model to isometric tetanusL = ones(1000,1);          % Isometric length, (constant = 1 unit)   t = linspace(0,5,1000)';   % Time (0-5seconds)[P,H,Lse,Lce] = hill(L,t); % Compute P,H,Lse,Lce over timefigure(1)subplot(2,2,1); plot(t,L,'k'); axis([0 5 0 2]); xlabel('Time (sec)'); ylabel('Length/L0');subplot(2,2,2); plot(t,P,'r'); axis([0 5 0 200]); xlabel('Time (sec)'); ylabel('Force (mN/mm2)');subplot(2,2,3);plot(t,Lse,'g'); axis([0 5 0 1]);xlabel('Time (sec)'); ylabel('Lseries/L0');subplot(2,2,4); plot(t,Lce,'b'); axis([0 5 0 1]); xlabel('Time (sec)'); ylabel('Lcontractile/L0');figure(2)plot(t,H,'r');axis([0 5 0 50]); xlabel('Time (sec)'); ylabel('Heat/Length');%%clear;% EXAMPLE 2: Simulation of a step decrease in length imposed on a tetanized muscleL = [ones(400,1); ones(600,1)*0.95];   % Step decrease in length from 1 unit to 0.95unitst = linspace(0,5,1000)';               % Time (0-5seconds)[P,H,Lse,Lce] = hill(L,t);             % Compute P,H,Lse,Lce over time figure(3)subplot(2,2,1); plot(t,L,'k'); axis([0 5 0 2]); xlabel('Time (sec)'); ylabel('Length/L0');subplot(2,2,2); plot(t,P,'r'); axis([0 5 0 200]); xlabel('Time (sec)'); ylabel('Force (mN/mm2)');subplot(2,2,3); plot(t,Lse,'g'); axis([0 5 0 1]);xlabel('Time (sec)'); ylabel('Lseries/L0');subplot(2,2,4); plot(t,Lce,'b'); axis([0 5 0 1]); xlabel('Time (sec)'); ylabel('Lcontractile/L0');figure(4)plot(t,H,'r');axis([0 5 0 50]); xlabel('Time (sec)'); ylabel('Heat/Length');%%clear;% EXAMPLE 3 - Dependence of rate and amount of heat liberation on the %             rate of shortening. for i = 1:6        % Generating 6 different rates of decrease in length    ts = 100*i;    L = [ones(400,1);linspace(1,0.5,ts)';repmat(0.5,(600-ts),1)];    t = linspace(0,5,1000)';      % Time (0-5seconds)    [P,H,Lse,Lce] = hill(L,t);    % Compute P,H,Lse,Lce over time        figure(1)    subplot(2,2,1); hold on;    plot(t,L,'k'); axis([0 5 0 2]); xlabel('Time (sec)'); ylabel('Length/L0');    subplot(2,2,2); hold on;    plot(t,P,'r'); axis([0 5 0 200]); xlabel('Time (sec)'); ylabel('Force (mN/mm2)');    subplot(2,2,3); hold on;    plot(t,Lse,'g'); axis([0 5 0 1]);xlabel('Time (sec)'); ylabel('Lseries/L0');    subplot(2,2,4); hold on;    plot(t,Lce,'b'); axis([0 5 0 1]); xlabel('Time (sec)'); ylabel('Lcontractile/L0');    figure(2)    hold on; plot(t,H,'r');axis([0 5 0 50]); xlabel('Time (sec)'); ylabel('Heat/Length');    input('')     %Press enterend%%clear;% EXAMPLE 4 - Dependence of rate and amount of heat liberation on the%             distance of shortening. for i= 0:5        % Generating 6 different final lengths with constant rate of decrease    Lf = 0.9 - 0.1*i;    ts = fix((1-Lf)*1000);    L = [ones(400,1); linspace(1,Lf,ts)';repmat(Lf,(600-ts),1)];    t = linspace(0,5,1000)';         % Time (0-5seconds)    [P,H,Lse,Lce] = hill(L,t);       % Compute P,H,Lse,Lce over time            figure(1)    subplot(2,2,1); hold on    plot(t,L,'k'); axis([0 5 0 2]); xlabel('Time (sec)'); ylabel('Length/L0');    subplot(2,2,2); hold on    plot(t,P,'r'); axis([0 5 0 200]); xlabel('Time (sec)'); ylabel('Force (mN/mm2)');    subplot(2,2,3); hold on    plot(t,Lse,'g'); axis([0 5 0 1]);xlabel('Time (sec)'); ylabel('Lseries/L0');    subplot(2,2,4); hold on    plot(t,Lce,'b'); axis([0 5 0 1]); xlabel('Time (sec)'); ylabel('Lcontractile/L0');        figure(2)    hold on; plot(t,H,'r');axis([0 5 0 50]); xlabel('Time (sec)'); ylabel('Heat/Length');    input('')     %Press enter    end%%